import React from 'react'
import ReactEcharts from 'echarts-for-react'

class DoughnutChart extends React.Component {

  constructor(props) {
    super(props);

  }



  componentDidMount() {
    this.echartsInstance = this.echartsReactRef.getEchartsInstance();
    this.zr = this.echartsInstance.getZr();

    this.zr.on('updateAxisPointer', function (event) {
      console.log("ola")
      const xAxisInfo = event.axesInfo[0];
      if (xAxisInfo) {
        const dimension = xAxisInfo.value + 1;
        this.echartsInstance.setOption({
          series: {
            id: 'pie',
            label: {
              formatter: '{b}: {@[' + dimension + ']} ({d}%)'
            },
            encode: {
              value: dimension,
              tooltip: dimension
            }
          }
        });
      }
    });
  }

    getOption = () => ({
        tooltip: {
            trigger: 'item'
          },
          legend: {
            top: '5%',
            left: 'center',
            textStyle: {
              fontSize: 16,
              fontFamily: 'Verdana, Geneva, Tahoma, sans-serif',
            },
          },
          series: [
            {
              name: 'Total de artigos',
              type: 'pie',
              radius: ['40%', '70%'],
              avoidLabelOverlap: false,
              itemStyle: {
                borderRadius: 10,
                borderColor: '#fff',
                borderWidth: 2
              },
              textStyle: {
                fontSize: 16,
                fontFamily: 'Verdana, Geneva, Tahoma, sans-serif',
              },
              label: {
                show: false,
                position: 'center'
              },
              emphasis: {
                label: {
                  show: true,
                  fontSize: '40',
                  fontWeight: 'bold'
                }
              },
              labelLine: {
                show: false
              },
              data: [
                { value: 548, name: 'Estigmatizante' },
                { value: 500, name: 'Literal' },
              ]
            }
          ]
    });

    render() {
        return (
            <ReactEcharts
                option={this.getOption()}
                style={{ height: "600px", width: "500px", marginLeft:"50px", marginTop:"80px"}}
                 ref={(e) => {
                            this.echartsReactRef = e;
                        }}
            />
        )};
}

export default DoughnutChart
