import React from 'react'
import ReactEcharts from 'echarts-for-react'
import topic0 from "assets/img/logo/logo_3.png"
import {
  CardImg
} from 'reactstrap';

class TopicsChart extends React.Component {

    getOption = () => ({
        title: {
            subtext: 'Artigos estigmatizantes',

            left: 'center'
          },
          tooltip: {
            trigger: 'item',
            formatter: [
              '{title|{b}}{abg|}',
              '{Sunny|}'
            ].join('\n'),
            backgroundColor: '#eee',
            borderColor: '#777',
            borderWidth: 1,
            borderRadius: 4,
            rich: {
              title: {
                color: '#eee',
                align: 'center'
              },
              abg: {
                backgroundColor: '#333',
                width: '100%',
                align: 'right',
                height: 25,
                borderRadius: [4, 4, 0, 0]
              },
              Sunny: {
                height: 300,


                align: 'left',
                backgroundColor: {
                  image: topic0,
                }

              },
              weatherHead: {
                color: '#333',
                height: 24,
                align: 'left'
              },
              hr: {
                borderColor: '#777',
                width: '100%',
                borderWidth: 0.5,
                height: 0
              },
              value: {
                width: 20,
                padding: [0, 20, 0, 30],
                align: 'left'
              },
              valueHead: {
                color: '#333',
                width: 20,
                padding: [0, 20, 0, 30],
                align: 'center'
              },
              rate: {
                width: 40,
                align: 'right',
                padding: [0, 10, 0, 0]
              },
              rateHead: {
                color: '#333',
                width: 40,
                align: 'center',
                padding: [0, 10, 0, 0]
              }
            }
          },
          legend: {
            bottom: 10,
            left: 'center',
            data: ['CityA', 'CityB', 'CityD', 'CityC', 'CityE']
          },
          series: [
            {
              type: 'pie',
              radius: '65%',
              center: ['50%', '50%'],
              selectedMode: 'single',
              data: [
                {
                  value: 1548,
                  name: 'CityE',
                  label: {

                  }
                },
                { value: 735, name: 'CityC' },
                { value: 510, name: 'CityD' },
                { value: 434, name: 'CityB' },
                { value: 335, name: 'CityA' }
              ],
              emphasis: {
                itemStyle: {
                  shadowBlur: 10,
                  shadowOffsetX: 0,
                  shadowColor: 'rgba(0, 0, 0, 0.5)'
                }
              }
            }
          ]
      });

      render(){
        return (
            <ReactEcharts
                option={this.getOption()}
                style={{ height: "500px", width: "1200px", marginLeft:"0px", marginTop:"30px"}}
            />
        );
      }




}

export default TopicsChart
