import React from 'react';
import Page from 'components/Page';
import 'styles/components/_jornais.scss';
import classNames from 'classnames';
import news1 from 'assets/img/news/expresso.png';
import {
    Button,
    Modal,
    Row,
  ModalBody,
  ModalFooter,
  ModalHeader,
  } from 'reactstrap';


class CitiesSlider extends React.Component {
    state = {
        modal: false,
        modal_backdrop: true,
        modal_nested_parent: false,
        modal_nested: false,
        backdrop: true,
      };

  toggle = modalType => () => {

    this.generate_text()

    if (!modalType) {
      return this.setState({
        modal: !this.state.modal,
      });
    }

    this.setState({
      [`modal_${modalType}`]: !this.state[`modal_${modalType}`],
    });


  };

    constructor(props) {
      super(props);

      this.IMAGE_PARTS = 4;

      this.changeTO = null;
      this.AUTOCHANGE_TIME = 4000;

      this.state = { activeSlide: -1, prevSlide: -1, sliderReady: false };
      this.modal = {
        title: "Título",
        text: "Notícia",
        url: "error"
      };
    }

    generate_text() {

        if (this.state.activeSlide==0) {
          this.modal.title="Descoberta científica abre a porta à prevenção da esquizofrenia";
          this.modal.text="Havia a dúvida, faltava a certeza e, na quinta-feira, um artigo publicado na conceituada revista especializada “Nature Communications” por uma equipa do Imperial e do King’s College, em Londres, de que faz parte o psiquiatra português Tiago Reis Marques, apresentou a evidência há muito buscada. Ao longo da última década, os investigadores que estudam a esquizofrenia levantavam a hipótese de o cérebro destes doentes ter menos sinapses — a forma como dois neurónios comunicam entre si — do que o cérebro de pessoas saudáveis. A utilização de um equipamento habitualmente usado em doentes oncológicos (PET — Tomografia de Emissão de Positrões) para captar imagens do cérebro de pessoas com esquizofrenia revelou estruturas visualmente mais uniformes em termos cromáticos. Mais importante do que a evidência encontrada é a porta que com ela se abre: a hipótese de se prevenir a esquizofrenia, doença complexa e altamente incapacitante e de cujas causas ainda se sabe pouco. “O impacto deste estudo reflete-se em dois principais domínios. Em primeiro lugar, permite-nos compreender melhor a neurobiologia da doença, isto é, o que se passa no cérebro de pessoas com esta doença mental. E, sobretudo, uma possível área terapêutica, com o desenvolvimento de fármacos que permitam aumentar ou evitar a redução do número de sinapses”, explica o médico português, de 43 anos, docente do Instituto de Psiquiatria do King’s College.";
          this.modal.url = "https://arquivo.pt/wayback/20200123200837/https://expresso.pt/sociedade/2020-01-19-Descoberta-cientifica-abre-a-porta-a-prevencao-da-esquizofrenia";
        }
        else if (this.state.activeSlide==1) {this.modal.text="xau"}

    }

    componentWillUnmount() {
      window.clearTimeout(this.changeTO);
    }

    componentDidMount() {

      setTimeout(() => {
        this.setState({ activeSlide: 0, sliderReady: true });
      }, 0);
    }

    runAutochangeTO() {
      this.changeTO = setTimeout(() => {
        this.changeSlides(1);
        this.runAutochangeTO();
      }, this.AUTOCHANGE_TIME);
    }

    changeSlides(change) {
      window.clearTimeout(this.changeTO);
      const { length } = this.props.slides;
      const prevSlide = this.state.activeSlide;
      let activeSlide = prevSlide + change;
      if (activeSlide < 0) activeSlide = length - 1;
      if (activeSlide >= length) activeSlide = 0;
      this.setState({ activeSlide, prevSlide });
    }


    render() {
      const { activeSlide, prevSlide, sliderReady } = this.state;
      return (
        <div className={classNames('slider', { 's--ready': sliderReady })}>

          <div className="slider__slides">
            {this.props.slides.map((slide, index) => (
              <div

                className={classNames('slider__slide', { 's--active': activeSlide === index, 's--prev': prevSlide === index  })}
                key={slide.city}
                >

                <div className="slider__slide-content">
                  <h2 className="slider__slide-heading">Classificaria esta notícia como...?</h2>
                  <Row>
                  <Button color="primary" className="slider__slide-subheading">Não-estigmatizante</Button>
                  <Button color="warning" className="slider__slide-subheading2">Estigmatizante</Button>
                  </Row>
                  <p onClick={this.toggle()} className="slider__slide-readmore">Ler notícia completa</p>
                  <Modal
                  isOpen={this.state.modal}
                  toggle={this.toggle()}
                  className={this.props.className}>
                  <ModalHeader toggle={this.toggle()}>{this.modal.title}</ModalHeader>
                  <ModalBody>
                    {this.modal.text}
                  </ModalBody>
                  <ModalFooter>
                    <Button color="primary" onClick={()=> window.open(this.modal.url, "_blank")}>
                      Aceder à notícia no Arquivo.pt
                    </Button>{' '}

                  </ModalFooter>
                </Modal>
                </div>
                <div className="slider__slide-parts">
                  {[...Array(this.IMAGE_PARTS).fill()].map((x, i) => (
                    <div className="slider__slide-part" key={i}>
                      <div className="slider__slide-part-inner" style={{ backgroundImage: `url(${slide.img})` }} />
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
          <div className="slider__control" onClick={() => this.changeSlides(-1)} />
          <div className="slider__control slider__control--right" onClick={() => this.changeSlides(1)} />
        </div>
      );
    }
  }

  const slides = [
    {
      city: 'Paris',
      country: 'France',
      img: news1,
    },
    {
      city: 'Singapore',
      img: 'https://s3-us-west-2.amazonaws.com/s.cdpn.io/142996/singapore.jpg',
    },
    {
      city: 'Prague',
      country: 'Czech Republic',
      img: 'https://s3-us-west-2.amazonaws.com/s.cdpn.io/142996/prague.jpg',
    },
    {
      city: 'Amsterdam',
      country: 'Netherlands',
      img: 'https://s3-us-west-2.amazonaws.com/s.cdpn.io/142996/amsterdam.jpg',
    },
    {
      city: 'Moscow',
      country: 'Russia',
      img: 'https://s3-us-west-2.amazonaws.com/s.cdpn.io/142996/moscow.jpg',
    },
  ];

const JornaisPage = () => {
  return (
    <Page title="Modals" breadcrumbs={[{ name: 'modals', active: true }]}>
 <CitiesSlider slides={slides} />
    </Page>
  );
};

export default JornaisPage;
