import React from 'react'
import { useTheme } from '@mui/system'
import ReactEcharts from 'echarts-for-react'

const DoughnutChart = ({ height, color = [] }) => {
    const theme = useTheme()

    const option = {
        legend: {
            show: true,
            itemGap: 20,
            icon: 'circle',
            bottom: 0,
            textStyle: {
                fontFamily: "Verdana, Geneva, Tahoma, sans-serif",
                fontSize: "16px",
            },
        },
        tooltip: {
            show: false,
            trigger: 'item',
            formatter: '{a} <br/>{b}: {c} ({d}%)',
        },
        xAxis: [
            {
                axisLine: {
                    show: false,
                },
                splitLine: {
                    show: false,
                },
            },
        ],
        yAxis: [
            {
                axisLine: {
                    show: false,
                },
                splitLine: {
                    show: false,
                },
            },
        ],

        series: [
            {
                name: 'Traffic Rate',
                type: 'pie',
                radius: ['45%', '72.55%'],
                center: ['50%', '50%'],
                avoidLabelOverlap: false,
                hoverOffset: 5,
                stillShowZeroSum: false,
                label: {
                    normal: {
                        show: false,
                        position: 'center', // shows the description data to center, turn off to show in right side
                        textStyle: {
                            textStyle: {
                                fontFamily: "Verdana, Geneva, Tahoma, sans-serif",
                                fontSize: '16',
                            },
                        },
                        formatter: '{a}',
                    },
                    emphasis: {
                        show: true,
                        textStyle: {
                            textStyle: {
                                fontFamily: "Verdana, Geneva, Tahoma, sans-serif",
                                fontSize: '16',
                            },
                            // color: "rgba(15, 21, 77, 1)"
                        },
                        formatter: '{b} \n({c}%)',
                    },
                },
                labelLine: {
                    normal: {
                        show: false,
                    },
                },
                data: [
                    {
                        value: 55,
                        name: 'Estigmatizante',
                    },
                    {
                        value: 45,
                        name: 'Literal',
                    },

                ],
                itemStyle: {
                    emphasis: {
                        shadowBlur: 20,
                        shadowOffsetX: 0,
                        shadowColor: 'rgba(0, 0, 0, 0.5)',
                    },
                },
            },
        ],
    }

    return (
        <ReactEcharts
            style={{ height: height, width: "400px"}}
            option={{
                ...option,
                color: [...color],
            }}
        />
    )
}

export default DoughnutChart
