import React from 'react';

import { gsap } from 'gsap';
import logoArquivo from 'assets/img/logo/arquivo.png';
import logoPaper from 'assets/img/logo/paper.png';
import logoCode from 'assets/img/logo/code.png';

import Page from 'components/Page';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardHeader from '@mui/material/CardHeader';
import CssBaseline from '@mui/material/CssBaseline';
import Grid from '@mui/material/Grid';
import Toolbar from '@mui/material/Toolbar';
import Typography from 'components/Typography';
import Link from '@mui/material/Link';
import GlobalStyles from '@mui/material/GlobalStyles';
import Container from '@mui/material/Container';

import 'styles/components/_sources.scss';

import {
  CardImg,
} from 'reactstrap';
const { useRef, useState, useEffect, createRef } = React






function Copyright(props) {
  return (
    <Typography variant="body2" color="text.secondary" align="center" {...props}>
      {'Copyright © 2022   Implementado na '}
      <a style={{color:"#20F624"}} href="https://www.ua.pt">Universidade de Aveiro</a>
    </Typography>
  );
}

const tiers = [
  {
    title: 'Projeto',
    image: logoCode,
    description: [
      'Código-aberto do projeto',
    ],
    buttonText:  <a href="https://www.ua.pt">Aceder ao projeto</a>,
    buttonVariant: 'outlined',
  },
  {
    title: 'Relatório técnico',
    image: logoPaper,
    description: [
      'Relatório técnico do projeto',,
    ],
    buttonText: <a href="https://www.ua.pt">Aceder ao relatório</a>,
    buttonVariant: 'outlined',
  },
  {
    title: 'Arquivo.pt',
    image: logoArquivo,
    description: [
      'Fonte oficial dos dados',
    ],
    buttonText: <a href="https://www.arquivo.pt">Aceder ao Arquivo.pt</a>,
    buttonVariant: 'outlined',
  },

];

const footers = [
  {
    title: 'Company',
    description: ['Team', 'History', 'Contact us', 'Locations'],
  },
  {
    title: 'Features',
    description: [
      'Cool stuff',
      'Random feature',
      'Team feature',
      'Developer stuff',
      'Another one',
    ],
  },
  {
    title: 'Resources',
    description: ['Resource', 'Resource name', 'Another resource', 'Final resource'],
  },
  {
    title: 'Legal',
    description: ['Privacy policy', 'Terms of use'],
  },
];

const Menu = ({items}) => {
  const $root = useRef()
  const $indicator1 = useRef()
  const $indicator2 = useRef()
  const $items = useRef(items.map(createRef))
  const [ active, setActive ] = useState(0)

  const animate = () => {
    const menuOffset = $root.current.getBoundingClientRect()
    const activeItem = $items.current[active].current
    const { width, height, top, left } = activeItem.getBoundingClientRect()

    const settings = {
      x: left - menuOffset.x,
      y: top - menuOffset.y,
      width: width,
      height: height,
      backgroundColor: items[active].color,
      ease: 'elastic.out(.7, .7)',
      duration: .8
    }

    gsap.to($indicator1.current, {
      ...settings,
    })

    gsap.to($indicator2.current, {
      ...settings,
      duration: 1
    })


  }

  useEffect(() => {
    animate()
    window.addEventListener('resize', animate)

    return (() => {
      window.removeEventListener('resize', animate)
    })    
  }, [active])

  return (
    <div
      ref={$root}
      className="menu"
    >
      {items.map((item, index) => (
        <a
          key={item.name}
          ref={$items.current[index]}
          className={`item ${active === index ? 'active' : ''}`}
          onMouseEnter={() => {
            setActive(index)
          }}
          href={item.href}
         >
          {item.name}
        </a>
      ))}
      <div
        ref={$indicator1}
        className="indicator"
       />
      <div
        ref={$indicator2}
        className="indicator"
       />
    </div>
  )
}

const SourcesPage = () => {
  return (
    <div style={{backgroundColor:"#7636EA"}}>
    <React.Fragment>
      <GlobalStyles styles={{ ul: { margin: 0, padding: 0, listStyle: 'none' } }} />
      <CssBaseline />

      {/* Hero unit */}
      <Container disableGutters maxWidth="sm" component="main" sx={{ pt: 6, pb: 6 }}>

      <p className="authorsTitle">Autores</p>
        <small className="text-dark">
        <Typography type="h6" align="center" component="p">
          Quickly build an effective pricing table for your potential customers with
          this layout. It&apos;s built with default MUI components with little
          customization.
        </Typography>
        </small>
      </Container>
      {/* End hero unit */}
      <Container maxWidth="md" component="main">
        <Grid container spacing={5} alignItems="flex-end">
          {tiers.map((tier) => (
            // Enterprise card is full width at sm breakpoint
            <Grid
              item
              key={tier.title}
              xs={12}
              sm={tier.title === 'Enterprise' ? 12 : 6}
              md={4}
            >
              <Card>
                <CardHeader
                  title={tier.title}
                  subheader={tier.subheader}
                  titleTypographyProps={{ align: 'center' }}

                  subheaderTypographyProps={{
                    align: 'center',
                  }}
                  sx={{
                    backgroundColor: (theme) =>
                      theme.palette.mode === 'light'
                        ? theme.palette.grey[200]
                        : theme.palette.grey[700],
                  }}
                />
                <CardContent>
                  <Box
                    sx={{
                      display: 'flex',
                      justifyContent: 'center',
                      alignItems: 'baseline',
                      mb:2,

                    }}
                  >
                   <CardImg src={tier.image} style={{margin:20}}  />
                  </Box>
                  <ul>
                    {tier.description.map((line) => (
                      <Typography
                        component="li"
                        variant="subtitle1"
                        align="center"
                        key={line}
                      >
                        {line}
                      </Typography>
                    ))}
                  </ul>
                </CardContent>
                <CardActions>
                  <Button fullWidth variant={tier.buttonVariant}>
                    {tier.buttonText}

                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
      <Container
        maxWidth="md"
        component="footer"
        sx={{
          borderTop: (theme) => `1px solid ${theme.palette.divider}`,
          mt: 25,
          py: [3, 3],
        }}
      >

        <Copyright  />
      </Container>

    </React.Fragment>
    </div>
  );
}

export default SourcesPage;
