import React from 'react';
import Page from 'components/Page';
import 'styles/components/_dash.scss';
import FadeIn from 'components/FadeIn';
import Row from 'reactstrap/lib/Row';
import logo1 from "assets/img/logo/logo_1.png"
import logo2 from "assets/img/logo/logo_2.png"
import logo3 from "assets/img/logo/logo_3.png"
import { NumberWidget, IconWidget } from 'components/Widget';
import CardContent from '@mui/material/CardContent';
import DoughnutChart from 'components/Graphs/DoughnutChart'
import BarChart from 'components/Graphs/BarChart'
import PieChart from 'components/Graphs/PieChart'
import {
    Card,
    Col,
    CardImg
  } from 'reactstrap';
import * as echarts from 'echarts';

class DashPage extends React.Component {


  constructor() {
    super();
  }





  render() {



  return (
    <Page>
    <Row >
    <div style={{backgroundImage:'linear-gradient(#ffd639, white)', width:"100%"}}>
    <FadeIn>
    <p className="dashTitle">Resultados</p>

    <Row>
            <Card className='dashCard1'>
                <CardContent>
                    <Row>
                    <CardImg src={logo2} style={{width:"80px", marginTop:"20px", marginLeft:"35px"}}/>
                    <p className="dashCard2Title">9</p>
                    <p className="dashCardSubtitle"> Jornais analisados </p>
                    </Row>
                </CardContent>
            </Card>
            <Card className='dashCard1'>
                <CardContent>
                    <Row>
                    <CardImg src={logo1} style={{width:"80px", marginTop:"20px", marginLeft:"35px"}}/>
                    <p className="dashCardTitle">997 </p>
                    <p className="dashCardSubtitle">Artigos recolhidos </p>
                    </Row>
                </CardContent>
            </Card>
            <Card className='dashCard3'>
                <CardContent>
                    <Row>
                    <CardImg src={logo3} style={{width:"100px",height:"80px", marginTop:"0px", marginLeft:"35px"}}/>
                    <p className="dashCard3Title">8 </p>
                    <p className="dashCard3Subtitle"> Algoritmos de Intelig√™ncia Artificial</p>
                    <NumberWidget
                      number=""
                      color="blue"
                      progress={{
                        value: 93,
                        label: 'Precis√£o do melhor algoritmo',
                      }}
                    />
                    </Row>
                </CardContent>
            </Card>
    </Row>

    <Col>
              <Card className="dashCard2">
                <Row>
                <p className="titleGraph">Todos os artigos</p>
                <p className="subtitleGraph">Classifica√ß√£o global dos artigos e agrupamento por ano de arquivamento no Arquivo.pt</p>
                </Row>
                <Row>
                <DoughnutChart height="500px"
                                    color={[
                                        "red",
                                        "black",]}>
                </DoughnutChart>
                <BarChart>

                </BarChart>
                </Row>
                </Card>
              <Card className="dashCard2">
                <Row>
                <p className="titleGraph">Artigos estigmatizantes por jornal</p>
                <p className="subtitleGraph">Artigos estigmatizantes por jornal e evolu√ß√£o da sua quantidade ao longo dos anos de arquivamento no Arquivo.pt</p>
                </Row>
                <Row onMouseEnter={() => this.change}>
                  <PieChart id="pie"></PieChart>
                </Row>
                </Card>

      </Col>


    </FadeIn>
    </div>
    </Row>
    </Page>
  );
  }
}

var chartDom = document.getElementById('pie');
var myChart = echarts.init(chartDom);

myChart.on('updateAxisPointer', function (event) {
    const xAxisInfo = event.axesInfo[0];
    if (xAxisInfo) {
      const dimension = xAxisInfo.value + 1;
      myChart.setOption({
        series: {
          id: 'pie',
          label: {
            formatter: '{b}: {@[' + dimension + ']} ({d}%)'
          },
          encode: {
            value: dimension,
            tooltip: dimension
          }
        }
      });
    }
  });


export default DashPage;
