import React from 'react'
import ReactEcharts from 'echarts-for-react'

function DoughnutChart({options}){
  const myChart = useRef(null)

    useEffect(() => {
      const chart = echarts.init(myChart.current)
      chart.setOption(options)
      chart.on('updateAxisPointer', function (event) {
        const xAxisInfo = event.axesInfo[0];
        if (xAxisInfo) {
          const dimension = xAxisInfo.value + 1;
          chart.setOption({
            series: {
              id: 'pie',
              label: {
                formatter: '{b}: {@[' + dimension + ']} ({d}%)'
              },
              encode: {
                value: dimension,
                tooltip: dimension
              }
            }
          });
        }
      });

    }, [options, resizeObserver])






    options= {
        tooltip: {
            trigger: 'item'
          },
          legend: {
            top: '5%',
            left: 'center',
            textStyle: {
              fontSize: 16,
              fontFamily: 'Verdana, Geneva, Tahoma, sans-serif',
            },
          },
          series: [
            {
              name: 'Total de artigos',
              type: 'pie',
              radius: ['40%', '70%'],
              avoidLabelOverlap: false,
              itemStyle: {
                borderRadius: 10,
                borderColor: '#fff',
                borderWidth: 2
              },
              textStyle: {
                fontSize: 16,
                fontFamily: 'Verdana, Geneva, Tahoma, sans-serif',
              },
              label: {
                show: false,
                position: 'center'
              },
              emphasis: {
                label: {
                  show: true,
                  fontSize: '40',
                  fontWeight: 'bold'
                }
              },
              labelLine: {
                show: false
              },
              data: [
                { value: 548, name: 'Estigmatizante' },
                { value: 500, name: 'Literal' },
              ]
            }
          ]
    };

    return (
            <ReactEcharts

                style={{ height: "600px", width: "500px", marginLeft:"50px", marginTop:"80px"}}
            />
    )
}

export default DoughnutChart
