import React from 'react';
import Page from 'components/Page';
import 'styles/components/_jornais.scss';
import classNames from 'classnames';
import news1 from 'assets/img/news/expresso.png';
import Swal from 'sweetalert2'
import {
    Button,
    Modal,
    Row,
    Container,
  ModalBody,
  ModalFooter,
  ModalHeader,
  } from 'reactstrap';


class CitiesSlider extends React.Component {

  toggle = modalType => () => {

    this.generate_text()


  };

    constructor(props) {
      super(props);

      this.generate_answer_non = this.generate_answer_non.bind(this);
      this.generate_answer_yes = this.generate_answer_yes.bind(this);

      this.IMAGE_PARTS = 4;

      this.changeTO = null;
      this.AUTOCHANGE_TIME = 4000;

      this.state = { activeSlide: -1, prevSlide: -1, sliderReady: false };

