import React from 'react'
import ReactEcharts from 'echarts-for-react'
import topic0 from "assets/img/logo/logo_3.png"
import {
  CardImg
} from 'reactstrap';
import { Hidden } from '@mui/material';
import { blue } from '@mui/material/colors';

class TopicsChart extends React.Component {

  constructor(props) {
    super(props)
  }

  clicked = (event) => {
    let echarts_instance = this.refs.echarts_react.getEchartsInstance();

    echarts_instance.on('click', params => {
    if (params.name === 'ola') {
      console.log('title is double-clicked!')
    }})


  }

  EventsDict = {
    'click': this.clicked
  }

    getOption = () => ({
        title: {

            left: 'center'
          },

          legend: {
            bottom: 10,
            left: 'center',
            data: ['CityA', 'CityB', 'CityD', 'CityC', 'CityE']
          },
          series: [
            {
              type: 'pie',
              radius: ['45%', '60%'],
              selectedMode: 'single',
              data: [
                {
                  value: 1548,
                  name: 'Estigmatizante',
                  label: {
                    triggerEvent: true,
                    formatter: [
                      '{title|{b}}{abg|}',
                      '{caption|Top 5 artigos}',
                      '{hr|}',
                      '  {Sunny|}{value|Prisão perpétua para.. }{url|Aceder}',
                    ].join('\n'),
                    backgroundColor: '#eee',
                    borderColor: '#777',
                    borderWidth: 1,
                    width: 300,
                    borderRadius: 4,
                    rich: {
                      title: {
                        color: '#eee',
                        align: 'center'
                      },
                      abg: {
                        backgroundColor: '#333',
                        width: '100%',
                        align: 'right',
                        height: 25,
                        borderRadius: [4, 4, 0, 0]
                      },
                      Sunny: {
                        name: 'ola',
                        height: 30,
                        align: 'left',
                        backgroundColor: {
                          image: topic0
                        }
                      },
                      weatherHead: {
                        color: '#333',
                        height: 24,
                        align: 'left'
                      },
                      hr: {
                        borderColor: '#777',
                        width: '100%',
                        borderWidth: 0.5,
                        height: 0
                      },
                      caption: {
                        width: 20,
                        padding: [10, 100, 10, 20],
                        align: 'left',
                        fontWeight: 800,
                      },
                      value: {
                        padding: [10,0,10,10],
                        align: 'left',
                        fontWeight: 500,
                      },
                      valueHead: {
                        color: '#333',
                        width: 20,
                        padding: [0, 20, 0, 30],
                        align: 'center'
                      },
                      url: {
                        width:50,
                        height:20,
                        padding: [0,0,0,100],
                        backgroundColor: "blue",

                        align: 'right',
                        padding: [0, 10, 0, 0]
                      },
                      rateHead: {
                        color: '#333',
                        width: 40,
                        align: 'center',
                        padding: [0, 10, 0, 0]
                      }
                    }
                  }
                },
                { value: 735, name: 'CityC'},
                { value: 510, name: 'CityD'},
                { value: 434, name: 'CityB',},
                { value: 335, name: 'CityA',}
              ],
              emphasis: {
                itemStyle: {
                  shadowBlur: 10,
                  shadowOffsetX: 0,
                  shadowColor: 'rgba(0, 0, 0, 0.5)'

                }
              }
            }
          ]
      });

      render(){
        return (
            <ReactEcharts
                option={this.getOption()}
                style={{ height: "900px", width: "1300px", marginLeft:"0px", marginTop:"30px"}}
                onEvents={this.EventsDict}
                ref='echarts_react'
            />
        );
      }




}

export default TopicsChart
