import React from 'react'
import ReactEcharts from 'echarts-for-react'
import topic0 from "assets/img/logo/logo_3.png"
import {
  CardImg
} from 'reactstrap';
import { Hidden } from '@mui/material';

class TopicsChart extends React.Component {

    getOption = () => ({
        title: {

            left: 'center'
          },
          tooltip: {
            trigger: 'item',
            formatter: '{a} <br/>{b} : {c} ({d}%)'
          },
          legend: {
            bottom: 10,
            left: 'center',
            data: ['CityA', 'CityB', 'CityD', 'CityC', 'CityE']
          },
          series: [
            {
              type: 'pie',
              radius: ['45%', '60%'],
              selectedMode: 'single',
              data: [
                {
                  value: 1548,
                  name: 'CityE',

                  label: { formatter: [
                    '{titulo}',
                    '{hr|}',
                    '  {ola aquilo é prisão perpetua para ti que foste ao tribunal}',

                  ].join('\n'),
                  backgroundColor: '#eee',
                  borderColor: '#777',
                  borderWidth: 1,
                  borderRadius: 4,
                  rich: {
                    title: {
                      color: '#eee',
                      align: 'center'
                    },
                    titulo: {
                        name: "kdjksnsknxs",

                    },
                    abg: {
                      backgroundColor: '#333',
                      width: '100%',
                      align: 'right',
                      height: 25,
                      borderRadius: [4, 4, 0, 0]
                    },
                    Sunny: {
                      height: 30,
                      align: 'left',

                    },
                    Cloudy: {
                      height: 30,
                      align: 'left',

                    },
                    Showers: {
                      height: 30,
                      align: 'left',

                    },
                    weatherHead: {
                      color: '#333',
                      height: 24,
                      align: 'left'
                    },
                    hr: {
                      borderColor: '#777',
                      width: '100%',
                      borderWidth: 0.5,
                      height: 0
                    },
                    value: {
                      width: 20,
                      padding: [0, 20, 0, 30],
                      align: 'left'
                    },
                    valueHead: {
                      color: '#333',
                      width: 20,
                      padding: [0, 20, 0, 30],
                      align: 'center'
                    },
                    rate: {
                      width: 40,
                      align: 'right',
                      padding: [0, 10, 0, 0]
                    },
                    rateHead: {
                      color: '#333',
                      width: 40,
                      align: 'center',
                      padding: [0, 10, 0, 0]
                    }
                  }
                }

                },
                { value: 735, name: 'CityC'},
                { value: 510, name: 'CityD'},
                { value: 434, name: 'CityB',},
                { value: 335, name: 'CityA',}
              ],
              emphasis: {
                itemStyle: {
                  shadowBlur: 10,
                  shadowOffsetX: 0,
                  shadowColor: 'rgba(0, 0, 0, 0.5)'

                }
              }
            }
          ]
      });

      render(){
        return (
            <ReactEcharts
                option={this.getOption()}
                style={{ height: "900px", width: "1300px", marginLeft:"0px", marginTop:"30px"}}
            />
        );
      }




}

export default TopicsChart
