import React from 'react';
import Page from 'components/Page';
import 'styles/components/_database.scss';
import FadeIn from 'components/FadeIn';
import Table, { AvatarCell, SelectColumnFilter, Topic, Url } from 'components/Table/Table'
import {csv} from 'd3-fetch';
import Row from 'reactstrap/lib/Row';

class DatabasePage extends React.Component {


  constructor() {
    super();
  }

  render() {
  return (
    <Page>
    <Row >
    <div style={{backgroundImage:'linear-gradient(#ffd639,white)', width:"100%"}}>
    <FadeIn>
      <p className="databaseTitle">Todos os artigos</p>
      </FadeIn> 
      <FadeIn>
      <p className="databaseSubheading">
        Os principais recursos e referências referentes a este projeto podem ser encontrados abaixo.
      </p>
      </FadeIn>
        <div className="divTable">
        <FadeIn delay="500">
        <Table columns={columns} data={data} />
        </FadeIn>
        </div>



    </div>
    </Row>
    </Page>
  );
  }
}

const columns = [
  {
    Header: "Título",
    accessor: 'title',

  },
  {
    Header: "Acesso",
    accessor: 'url',
    Cell: Url,
  },
  {
    Header: "Tópico",
    accessor: 'topic',
    Filter: SelectColumnFilter,  // new
    filter: 'includes',
    Cell: Topic,
  },
  {
    Header: "Ano",
    accessor: 'year',
    Filter: SelectColumnFilter,  // new
    filter: 'includes',

  },
  {
    Header: "Jornal",
    accessor: 'journal',
    Filter: SelectColumnFilter,  // new
    filter: 'includes',
  },
  {
      Header: "Sentido",
    accessor: 'label',
      Filter: SelectColumnFilter,  // new
      filter: "includes",
    },
]

const data = [{"title": "Prisão perpétua para homem que tentou assassinar senadora", "journal": "Público", "year": "2012", "url": "https://arquivo.pt/wayback/20121230181331/http://publico.pt/mundo/noticia/homem-que-tentou-assassinar-senadora-norteamericana-condenado-a-sete-prisoes-perpetuas-1571761", "topic": "Crime", "label": "literal"}, {"title": "John Nash, matemático de Uma Mente Brilhante, morre em acidente", "journal": "Público", "year": "2016", "url": "https://arquivo.pt/wayback/20160117223452/http://publico.pt/economia/noticia/john-nash-matematico-de-uma-mente-brilhante-morre-em-acidente-1696715", "topic": "Cinema e Teatro", "label": "literal"}, {"title": "O mito da reeleição mínima garantida", "journal": "Público", "year": "2011", "url": "https://arquivo.pt/wayback/20110121142608/http://publico.pt/Presidenciais2011/Noticias/Analise/o-mito-da-reeleicao-minima-garantida_1475944", "topic": "Política", "label": "estigma"}, {"title": "Morreu Rita Levi-Montalcini, a grande dama da ciência italiana", "journal": "Público", "year": "2013", "url": "https://arquivo.pt/wayback/20130117170513/http://publico.pt/ciencia/noticia/morreu-rita-levimontalcini-a-grande-dama-da-ciencia-italiana-1579018", "topic": "Saúde", "label": "literal"}, {"title": "Por trás da porta amarela, há um homem com problemas psicológicos", "journal": "Público", "year": "2015", "url": "https://arquivo.pt/wayback/20150420143056/http://publico.pt/sociedade/noticia/por-tras-da-porta-amarela-ha-um-homem-com-problemas-psicologicos-1662325", "topic": "Crime", "label": "literal"}, {"title": "Gregorio Duvivier de todas as maneiras e sem estilo", "journal": "Público", "year": "2015", "url": "https://arquivo.pt/wayback/20151104230446/http://publico.pt/culturaipsilon/noticia/gregorio-duvivier-de-todas-as-maneiras-e-sem-estilo-1709137", "topic": "Cinema e Teatro", "label": "estigma"}, {"title": "Ângelo de Sousa: Viver, esperar, talvez pintar", "journal": "Público", "year": "2013", "url": "https://arquivo.pt/way