import React from 'react'
import ReactEcharts from 'echarts-for-react'

class PieChart extends React.Component {

    getOption = () => ({
        legend: {},
        tooltip: {
          trigger: 'axis',
          showContent: false
        },
        dataset: {
          source: [
            ['product', '2012', '2013', '2014', '2015', '2016', '2017', '2018', '2019', '2020', '2021'],
            ['Público', 56.5, 82.1, 88.7, 70.1, 53.4, 85.1],
            ['Observador', 51.1, 51.4, 55.1, 53.3, 73.8, 68.7],
            ['Diário de Notícias', 40.1, 62.2, 69.5, 36.4, 45.2, 32.5],
            ['Expresso', 25.2, 37.1, 41.2, 8, 33.9, 49.1],
            ['Correio da Manhã', 25.2, 37.1, 41.2, 18, 13.9, 49.1],
            ['Jornal de Notícias', 50.2, 100.1, 41.2, 18, 33.9, 100.1],
            ['Visão', 5.2, 37.1, 41.2, 18, 33.9, 49.1],
            ['Sábado', 25.2, 7.1, 41.2, 18, 3.9, 49.1],
            ['A Bola', 15.2, 3.1, 41.2, 18, 33.9, 49.1]
          ]
        },
        xAxis: { type: 'category' },
        yAxis: { gridIndex: 0 },
        grid: { top: '55%' },
        series: [
          {
            type: 'line',
            smooth: true,
            seriesLayoutBy: 'row',
            emphasis: { focus: 'series' }
          },
          {
            type: 'line',
            smooth: true,
            seriesLayoutBy: 'row',
            emphasis: { focus: 'series' }
          },
          {
            type: 'line',
            smooth: true,
            seriesLayoutBy: 'row',
            emphasis: { focus: 'series' }
          },
          {
            type: 'line',
            smooth: true,
            seriesLayoutBy: 'row',
            emphasis: { focus: 'series' }
          },
          {
            type: 'line',
            smooth: true,
            seriesLayoutBy: 'row',
            emphasis: { focus: 'series' }
          },
          {
            type: 'line',
            smooth: true,
            seriesLayoutBy: 'row',
            emphasis: { focus: 'series' }
          },
          {
            type: 'line',
            smooth: true,
            seriesLayoutBy: 'row',
            emphasis: { focus: 'series' }
          },
          {
            type: 'line',
            smooth: true,
            seriesLayoutBy: 'row',
            emphasis: { focus: 'series' }
          },
          {
            type: 'line',
            smooth: true,
            seriesLayoutBy: 'row',
            emphasis: { focus: 'series' }
          },
          {
            type: 'pie',
            id: 'pie',
            radius: '30%',
            center: ['50%', '25%'],
            emphasis: {
              focus: 'self'
            },
            label: {
              formatter: '{b}: {@2012} ({d}%)'
            },
            encode: {
              itemName: 'product',
              value: '2012',
              tooltip: '2012'
            }
          }
        ]
      });

      render(){
        return (
            <ReactEcharts
                option={this.getOption()}
                style={{ height: "800px", width: "100%", marginTop: "70px" }}
            />
        );
      }




}

export default PieChart
