import React from 'react';
import { gsap } from 'gsap';
import logoArquivo from 'assets/img/logo/arquivo.png';
import logoPaper from 'assets/img/logo/paper.png';
import logoCode from 'assets/img/logo/code.png';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardHeader from '@mui/material/CardHeader';
import CssBaseline from '@mui/material/CssBaseline';
import Grid from '@mui/material/Grid';
import Typography from 'components/Typography';
import Container from '@mui/material/Container';

import 'styles/components/_sources.scss';
import FadeIn from 'components/FadeIn';

import {
  CardImg,
} from 'reactstrap';
import Row from 'reactstrap/lib/Row';
const { useRef, useState, useEffect, createRef } = React






function Copyright(props) {
  return (
    <Typography style={{color:"white", borderWidth:"350px"}} variant="body2" align="center" {...props}>
      {'Copyright © 2022   Implementado na '}
      <a style={{color:"#20F624", fontWeight: "bolder"}} href="https://www.ua.pt">Universidade de Aveiro</a>
    </Typography>
  );
}

const tiers = [
  {
    title: 'Projeto',
    image: logoCode,
    description: [
      'Código-aberto do projeto',
    ],
    buttonText:  <a className="buttonText" href="https://www.ua.pt">Aceder ao projeto</a>,
    buttonVariant: 'outlined',
  },
  {
    title: 'Relatório técnico',
    image: logoPaper,
    description: [
      'Relatório técnico do projeto',,
    ],
    buttonText: <a className="buttonText" href="https://www.ua.pt">Aceder ao relatório</a>,
    buttonVariant: 'outlined',
  },
  {
    title: 'Arquivo.pt',
    image: logoArquivo,
    description: [
      'Fonte oficial dos dados',
    ],
    buttonText: <a className="buttonText" href="https://www.arquivo.pt">Aceder ao Arquivo.pt</a>,
    buttonVariant: 'outlined',
  },

];

const tiers2 = [
  {
    title: 'Projeto',
    image: logoCode,
    description: [
      'Código-aberto do projeto',
    ],
    buttonText:  <a className="buttonText" href="https://www.ua.pt">Aceder ao projeto</a>,
    buttonVariant: 'outlined',
  },
  {
    title: 'Relatório técnico',
    image: logoPaper,
    description: [
      'Relatório técnico do projeto',,
    ],
    buttonText: <a className="buttonText" href="https://www.ua.pt">Aceder ao relatório</a>,
    buttonVariant: 'outlined',
  },
  {
    title: 'Arquivo.pt',
    image: logoArquivo,
    description: [
      'Fonte oficial dos dados',
    ],
    buttonText: <a className="buttonText" href="https://www.arquivo.pt">Aceder ao Arquivo.pt</a>,
    buttonVariant: 'outlined',
  },
  {
    title: 'Arquivo.pt',
    image: logoArquivo,
    description: [
      'Fonte oficial dos dados',
    ],
    buttonText: <a className="buttonText" href="https://www.arquivo.pt">Aceder ao Arquivo.pt</a>,
    buttonVariant: 'outlined',
  },

];




const SourcesPage = () => {
  return (
    <Row >
    <div style={{backgroundColor:"#7636EA", width:"100%"}}>
      <CssBaseline />
      {/* Hero unit */}
      <FadeIn>
      <p className="authorsTitle">RECURSOS</p>
      </FadeIn> 
      <FadeIn>
      <p className="subheading">
        Os principais recursos e referências referentes a este projeto podem ser encontrados abaixo.
      </p>
      </FadeIn>
      {/* End hero unit */}
      <Container  style={{marginTop:"70px"}}>
        <Grid container spacing={6} alignItems="flex-end">
          {tiers.map((tier) => (
            // Enterprise card is full width at sm breakpoint
            <Grid
              item
              key={tier.title}
              xs={4}
              sm={tier.title === 'Enterprise' ? 12 : 6}
              md={3.2}
            >
              <FadeIn  delay={450}>
              <Card className='recurso'>
                <CardHeader

                  title={tier.title}
                  subheader={tier.subheader}
                  titleTypographyProps={{ align: 'center' }}

                  subheaderTypographyProps={{
                    align: 'center',
                  }}
                  sx={{
                    backgroundColor: (theme) =>
                      theme.palette.mode === 'light'
                        ? theme.palette.grey[100]
                        : theme.palette.grey[200],
                  }}
                />
                <CardContent>
                  <Box
                    sx={{
                      display: 'flex',
                      justifyContent: 'center',
                      alignItems: 'baseline',
                      mb:2,

                    }}
                  >
                   <CardImg src={tier.image} style={{margin:20}}  />
                  </Box>
                  <p>
                    {tier.description.map((line) => (
                      <Typography
                        component="li"

                        align="center"
                        key={line}
                      >
                        {line}
                      </Typography>
                    ))}
                  </p>
                </CardContent>
                <CardActions>
                  <Button style={{borderColor:"#7636EA", borderWidth:"2px"}} fullWidth variant={tier.buttonVariant}>
                    {tier.buttonText}

                  </Button>
                </CardActions>
              </Card>
            </FadeIn>
            </Grid>
          ))}
        </Grid>
      </Container>
      <FadeIn>
      <p className="authorsTitle">Contactos</p>
      </FadeIn> 
      <FadeIn>

        <Grid container spacing={3} alignItems="flex-end">
          {tiers2.map((tier) => (
            // Enterprise card is full width at sm breakpoint
            <Grid
              item
              key={tier.title}
              xs={4}
              sm={tier.title === 'Enterprise' ? 12 : 6}
              md={2}
            >
              <FadeIn  delay={450}>
              <Card className='recurso'>
                <CardHeader

                  title={tier.title}
                  subheader={tier.subheader}
                  titleTypographyProps={{ align: 'center' }}

                  subheaderTypographyProps={{
                    align: 'center',
                  }}
                  sx={{
                    backgroundColor: (theme) =>
                      theme.palette.mode === 'light'
                        ? theme.palette.grey[100]
                        : theme.palette.grey[200],
                  }}
                />
                <CardContent>
                  <Box
                    sx={{
                      display: 'flex',
                      justifyContent: 'center',
                      alignItems: 'baseline',
                      mb:2,

                    }}
                  >
                   <CardImg src={tier.image} style={{margin:20}}  />
                  </Box>
                  <p>
                    {tier.description.map((line) => (
                      <Typography
                        component="li"

                        align="center"
                        key={line}
                      >
                        {line}
                      </Typography>
                    ))}
                  </p>
                </CardContent>
                <CardActions>
                  <Button style={{borderColor:"#7636EA", borderWidth:"2px"}} fullWidth variant={tier.buttonVariant}>
                    {tier.buttonText}

                  </Button>
                </CardActions>
              </Card>
            </FadeIn>
            </Grid>
          ))}
        </Grid>

      </FadeIn>
      <Container
        maxWidth="md"
        component="footer"
        sx={{
          borderTop: (theme) => `1px solid ${theme.palette.divider}`,
          mt: 25,
          py: [3, 3],
        }}
      >

        <Copyright  />
      </Container>


    </div>
    </Row>
  );
}

export default SourcesPage;
