import Page from 'components/Page';
import React from 'react';
import FadeIn from 'components/FadeIn';
import 'styles/components/_data.scss';
import oneinfive from 'assets/img/logo/1in5.png';
import Box from '@mui/material/Box';
import CardContent from '@mui/material/CardContent';
import Carousel from 'components/Widget/Carousel'
import card1 from 'assets/img/news/card1.png';
import card2 from 'assets/img/news/card2.png';
import{ useState, useRef } from 'react';
import { useSpring, animated } from 'react-spring';

import {
  Row,
  CardImg,
  Card,
  Col,
} from 'reactstrap';

function CardJournal({ children }) {
  // We add this ref to card element and use in onMouseMove event ...
  // ... to get element's offset and dimensions.
  const ref = useRef();

  // Keep track of whether card is hovered so we can increment ...
  // ... zIndex to ensure it shows up above other cards when animation causes overlap.
  const [isHovered, setHovered] = useState(false);

  const [animatedProps, setAnimatedProps] = useSpring(() => {
    return {
      // Array containing [rotateX, rotateY, and scale] values.
      // We store under a single key (xys) instead of separate keys ...
      // ... so that we can use animatedProps.xys.interpolate() to ...
      // ... easily generate the css transform value below.
      xys: [0, 0, 1],
      // Setup physics
      config: { mass: 10, tension: 400, friction: 40, precision: 0.00001 }
    };
  });

  return (
    <animated.div
      ref={ref}
      className="card"
      onMouseEnter={() => setHovered(true)}
      onMouseMove={({ clientX, clientY }) => {
        // Get mouse x position within card
        const x =
          clientX -
          (ref.current.offsetLeft -
            (window.scrollX || window.pageXOffset || document.body.scrollLeft));

        // Get mouse y position within card
        const y =
          clientY -
          (ref.current.offsetTop -
            (window.scrollY || window.pageYOffset || document.body.scrollTop));

        // Set animated values based on mouse position and card dimensions
        const dampen = 50; // Lower the number the less rotation
        const xys = [
          -(y - ref.current.clientHeight / 2) / dampen, // rotateX
          (x - ref.current.clientWidth / 2) / dampen, // rotateY
          1.07 // Scale
        ];

        // Update values to animate to
        setAnimatedProps({ xys: xys });
      }}
      onMouseLeave={() => {
        setHovered(false);
        // Set xys back to original
        setAnimatedProps({ xys: [0, 0, 1] });
      }}
      style={{
        // If hovered we want it to overlap other cards when it scales up
        zIndex: isHovered ? 2 : 1,
        // Interpolate function to handle css changes
        transform: animatedProps.xys.interpolate(
          (x, y, s) =>
            `perspective(600px) rotateX(${x}deg) rotateY(${y}deg) scale(${s})`
        )
      }}
    >
      {children}
    </animated.div>
  );
}



function HeroJournal ({ children }) {
  return (
    <div className="heroJournal">
      <div className="hero-bodyJournal">{children}</div>
    </div>
  );
}


const journals1 = [
  {
    image: card1
  },
  {
    image: card2
  },
  {
    image: card2
  },
  {
    image: card2
  },
  {
    image: card2
  },

];


const journals2 = [
  {
    image: card1
  },
  {
    image: card2
  },
  {
    image: card2
  },
  {
    image: card2
  },


];

const DataPage = () => {
  return (
    <Page title="" >
        <Row >
        <div style={{backgroundColor:"#f9c74f", width:"100%"}}>
      <FadeIn>
      <p className="dataTitle">Sobre o projeto</p>
      </FadeIn> 
      <FadeIn delay="500">
      <Row>
        <div className="fact1">
        <Col>
      <Box className="dataBox">
        <CardImg src={oneinfive} style={{margin:10}}  />
      </Box>
      <p className="fact1content">
        1 em cada 5 portugueses sofre de uma doença mental
      </p>
      </Col>
      </div>
      <div className="fact3">
      <p className="fact3content">
      Estigma (Organização Mundial da Saúde): 
      </p>
      </div>
      <div className="fact4">
        <p className="fact4content">
        Marca distintiva que, ao aliar-se às perturbações mentais, cria um ambiente de exclusão
social e discriminação perante a pessoa estigmatizada
        </p>
      </div>
      </Row>
      </FadeIn>
      <FadeIn delay="1000">
      <Row>
     <Card className='dataCard1'>

